import React from 'react'
import { Router, Route, Redirect } from 'react-router-dom'

import Nav from './Nav'
import Dashboard from './Dashboard'
import Login from './Login'
import CreateEmployee from './CreateEmployee'

const EMPRouter = () => {
  return (
    <Router >
      <div id='App'>
        <Nav handleMenuChange={this.props.handleMenuChange} currentTime={this.props.state.currentTime} user={this.props.state.user} />

        <Route
          exact
          path='/'
          render={() => {
            if (this.props.state.token) {
              return <Redirect to={{ pathname: '/dashboard' }} />
            }
            return <Redirect to={{ pathname: '/login' }} />
          }}
        />

        <Route
          path='/dashboard'
          render={() => {
            if (this.props.state.token) {
              return <Dashboard showMenu={this.props.state.showMenu} handleMenuChange={this.props.handleMenuChange} time={this.props.state.currentTime} />
            }
            return <Redirect to={{ pathname: '/login' }} />
          }}
        />

        <Route
          path='/login'
          render={() => {
            if (this.props.state.token) {
              return <Redirect to={{ pathname: '/dashboard' }} />
            }
            return <Login login={this.props.login} />
          }}
        />

        <Route
          path='/createEmployee'
          render={() => <CreateEmployee createUser={this.props.createEmployee} />}
        />

      </div>
    </Router>
  )
}

export default EMPRouter
