import React from 'react'
import { Header, Image, Menu, Segment, Sidebar, Grid, Container, Icon } from 'semantic-ui-react'
import './Dashboard.css'

import UserDashboard from './UserDashboard'

class Dashboard extends React.Component {

  state = {
    userType: 'employee',
    showMenu: true
  }

  componentWillReceiveProps = props => {
    const { showMenu } = props
    if (showMenu != this.state.showMenu) {
      this.setState({ showMenu })
    }
  }

  renderDashType() {
    if (this.state.userType === 'employee') {
      return <UserDashboard />
    } else {
      return <AdminDashboard />
    }
  }




  render() {
    return (
      <div id='Dashboard'>
        <Sidebar.Pushable as={Segment}>
          <Sidebar id='Sidebar' as={Menu} animation='scale down' icon='labeled' inverted vertical visible={this.state.showMenu} width='wide'>
            <Menu.Item id='Date'
              name={new Date().toDateString()}
            />
            <Image src='https://alchetron.com/cdn/steve-smith-american-dad-857b2ad3-ca14-4f31-923d-32b555d5546-resize-750.jpeg' size='small' circular />
            <Header as='h1'>Steven<br />Smith</Header>
            <Header as='h3'>Employee #001</Header>
            <Menu.Item as='a'>
              <Icon name='envelope outline' />
              Messages
            </Menu.Item>
            <Menu.Item as='a'>
              <Icon name='question circle' />
              Requests
            </Menu.Item>
          </Sidebar>

          <Sidebar.Pusher >
            <Container fluid>
              <UserDashboard />
            </Container>
          </Sidebar.Pusher>
        </Sidebar.Pushable>
      </div >
    )
  }
}

export default Dashboard
