import React from 'react'
import { Header, Image, Menu, Segment, Sidebar, Grid, Container, Icon } from 'semantic-ui-react'
import './Dashboard.css'

import EmployeeDashboard from './EmployeeDashboard'
import AdminDashboard from './AdminDash'
import DashSidebar from './DashSidebar'

class Dashboard extends React.Component {

  state = {
    showMenu: false,
    view: null
  }

  componentDidMount() {
    this.setState({ showMenu: this.props.showMenu })
  }

  componentWillReceiveProps = props => {
    const { showMenu } = props
    if (showMenu != this.state.showMenu) {
      this.setState({ showMenu })
    }
  }

  renderView = () => {
    switch (this.state.view) {
      case 'messages':
        return <Messages />
      case 'users':
        return <Employees />
      case 'jobs':
        return <Jobs />
      case 'timecard':
        return <Timecard />
      case 'schedule':
        return <Schedule />
      default:
        return <Messages />
    }
  }

  render() {
    return (
      <div id='Dashboard'>
        <Sidebar.Pushable as={Segment}>
          <Sidebar id='Sidebar' as={Menu} animation='push' icon='labeled' inverted vertical visible={this.state.showMenu} width='wide'>
            <Menu.Item id='Date' name={new Date().toDateString()} />
            <Image src={'./userAvatars/' + this.props.user.avatar} size='small' circular />
            <Header as='h1'>{this.props.user.firstName}<br />{this.props.user.lastName}</Header>
            <DashSidebar display={this.state.display} handleDisplayChange={this.handleDisplayChange} user={this.props.user} />
          </Sidebar>
          <Sidebar.Pusher dimmed={this.state.showMenu}>
            <Container fluid>
              {this.renderView()}
            </Container>
          </Sidebar.Pusher>
        </Sidebar.Pushable>
      </div >
    )
  }
}

export default Dashboard
