import React, { Component } from 'react'
import { Menu, Icon, Header, Image } from 'semantic-ui-react'

import './Nav.css'

export default class Nav extends Component {

  state = {
    showMenu: true,
    currentTime: this.props.currentTime
  }

  componentWillReceiveProps = props => {
    const { currentTime } = props
    if (currentTime != this.state.currentTime) {
      this.setState({ currentTime })
    }
  }

  render() {
    return (
      <Menu inverted id='Nav'>
        <Menu.Item onClick={this.props.handleMenuChange}>
          <div className='menuIcon'>
            <div className='bar' />
            <div className='bar' />
            <div className='bar' />
          </div>
        </Menu.Item>
        <Menu.Item id='Date'
          name={new Date().toDateString()}
        />
        <Menu.Menu position='right'>
          <Menu.Item>
            <Image src='https://alchetron.com/cdn/steve-smith-american-dad-857b2ad3-ca14-4f31-923d-32b555d5546-resize-750.jpeg' avatar />
            {'Hello ' + this.props.userFirstName + '!'}
          </Menu.Item>
          <Menu.Item
            name='log out'
            onClick={this.props.logout}
          >
            <Icon name='log out' size='large' />
          </Menu.Item>
        </Menu.Menu>
      </Menu>
    )
  }
}
