import { storeToken } from '../utils/handleToken'
import local from '../api/local'

const methods = {

  createEmployee: async (values, callback) => {
    const response = await local.post(`/employee/new`, values)
    const data = response.data
    if (!data.success) {
      callback(data.message)
    } else if (data.token) {
      callback(null, { token: data.token })
      storeToken(data.token)
    }
  },

  login: async (credentials, callback) => {
    const response = await local.post(`/employee/login`, credentials)
    const data = response.data
    if (!data.success) {
      callback(data.message)
    } else if (data.token) {
      callback(null, { token: data.token })
      storeToken(data.token)
    }
  },

  logout: async (token, callback) => {
    const response = await local.get(`/employee/logout?token=${token}`)
    const data = response.data
    if (!data.success) {
      callback(data.message)
    } else {
      callback(null, { token: '' })
    }
  }
}

export default methods
