import React from 'react'

class Clock extends React.Component {

  state = { time: new Date() }

  componentDidMount() {
    setInterval(() => {
      this.setState({ time: new Date() })
    }, 1000)
  }

  render() {
    return (
      <div id='clock'>
        {this.state.time.getHours()}:{this.state.time.getMinutes()}
      </div>
    )
  }
}

export default Clock
