import React from 'react'
import { Header, Menu, Icon } from 'semantic-ui-react'
import menuOptions from '../lib/menuOptions'


class DashSidebar extends React.Component {
  // menuOptions = [
  //   {
  //     name: 'messages',
  //     permissionLevel: ['Employee', 'Admin'],
  //     icon: 'envelope outline'
  //   },
  //   {
  //     name: 'employees',
  //     permissionLevel: ['Admin'],
  //     icon: 'user'
  //   },
  //   {
  //     name: 'jobs',
  //     permissionLevel: ['Admin'],
  //     icon: 'bolt'
  //   },
  //   {
  //     name: 'requests',
  //     permissionLevel: ['Employee', 'Admin'],
  //     icon: 'question circle'
  //   },
  //   {
  //     name: 'timecard',
  //     permissionLevel: ['Employee'],
  //     icon: 'clock'
  //   },
  //   {
  //     name: 'schedule',
  //     permissionLevel: ['Employee'],
  //     icon: 'calendar alternate'
  //   }
  // ]

  renderOptions = () => {
    switch (this.props.user.type) {
      case 'Employee':
        return menuOptions.map(option => {
          if (option.permissionLevel.indexOf('Employee') != -1) {
            return (
              <Menu.Item as='a' onClick={() => this.props.handleDisplayChange(option.name)}>
                <Icon name={option.icon} />
                {option.name}
              </Menu.Item>
            )
          }
        })
      case 'Admin':
      case 'Super':
        return menuOptions.map(option => {
          if (option.permissionLevel.indexOf('Admin') != -1) {
            return (
              <Menu.Item as='a' onClick={() => this.props.handleDisplayChange(option.name)}>
                <Icon name={option.icon} />
                {option.name}
              </Menu.Item>
            )
          }
        })
      default: return null
    }
  }


  render() {
    return (
      <React.Fragment>
        <Header as='h3'>Admin</Header>
        {this.renderOptions()}
      </React.Fragment>
    )
  }
}

export default DashSidebar
