import React from 'react'
import { setTime, createEmployee, login } from './lib/methods.js'
import { retrieveToken, storeToken } from './utils/handleToken.js'

import EMPRouter from './components/EMPRouter'

class App extends React.Component {

  state = {
    showMenu: false,
    user: {
      firstName: '',
      lastName: ''
    },
    time: new Date(),
    token: null
  }

  componentDidMount() {
    const token = retrieveToken('EMP')
    setTime()
  }

  handleMenuChange = () => this.setState({ showMenu: !this.state.showMenu })

  handleError(message) {
    console.log(message)
    // this.setState({ message })
    // setTimeout(() => {
    //   this.setState({ message: null })
    // }, 3000)
  }

  render() {
    return (
      <EMPRouter
        handleMenuChange={this.handleMenuChange}
        showMenu={this.state.showMenu}
        time={this.state.currentTime}
        employee={this.state.user}
        createEmployee={createEmployee}
        login={login}
      />
    )
  }
}

export default App
