import React from 'react'
import { Router, Route, Redirect } from 'react-router-dom'

import Nav from './Nav'
import Dashboard from './Dashboard'
import Login from './Login'
import CreateEmployee from './CreateEmployee'

const EMPRouter = props => {
  return (
    <Router >
      <div id='App'>
        <Nav handleMenuChange={props.handleMenuChange} time={props.time} user={props.user} />

        <Route
          exact
          path='/'
          render={() => {
            if (props.token) {
              return <Redirect to={{ pathname: '/dashboard' }} />
            }
            return <Redirect to={{ pathname: '/login' }} />
          }}
        />

        <Route
          path='/dashboard'
          render={() => {
            if (props.token) {
              return <Dashboard showMenu={props.showMenu} handleMenuChange={props.handleMenuChange} time={props.time} />
            }
            return <Redirect to={{ pathname: '/login' }} />
          }}
        />

        <Route
          path='/login'
          render={() => {
            if (props.token) {
              return <Redirect to={{ pathname: '/dashboard' }} />
            }
            return <Login login={props.login} />
          }}
        />

        <Route
          path='/createEmployee'
          render={() => <CreateEmployee createUser={props.createEmployee} />}
        />

      </div>
    </Router>
  )
}

export default EMPRouter
