import React from 'react'
import methods from './lib/methods'
import { retrieveToken, storeToken } from './utils/handleToken'

import EMPRouter from './components/EMPRouter'

class App extends React.Component {

  state = {
    showMenu: false,
    user: {
      firstName: '',
      lastName: ''
    },
    time: new Date(),
    token: null
  }

  componentDidMount() {
    const token = retrieveToken('EMP')
    this.setTime()
  }

  setTime = () => setInterval(() => this.setState({ currentTime: new Date() }), 1000)

  handleMenuChange = () => this.setState({ showMenu: !this.state.showMenu })

  createEmployee = values => {
    methods.createEmployee(values, (err, result) => {
      if (err) {
        console.log(err)
      } else {
        this.setState(result)
      }
    })
  }

  login = credentials => {
    methods.login(credentials, (err, result) => {
      if (err) {
        console.log(err)
      } else {
        this.setState(result)
      }
    })
  }

  logout = () => {
    this.setState({ token: null })
  }

  handleError(message) {
    console.log(message)
    // this.setState({ message })
    // setTimeout(() => {
    //   this.setState({ message: null })
    // }, 3000)
  }

  render() {
    return (
      <EMPRouter
        token={this.state.token}
        handleMenuChange={this.handleMenuChange}
        showMenu={this.state.showMenu}
        time={this.state.currentTime}
        employee={this.state.user}
        createEmployee={this.createEmployee}
        login={this.login}
      />
    )
  }
}

export default App
