import React from 'react'
import { Header, Menu, Icon } from 'semantic-ui-react'

class Sidebar extends React.Component {
  menuOptions = [
    {
      name: 'messages',
      permissionLevel: ['employee', 'admin'],
      icon: 'envelope outline'
    },
    {
      name: 'employees',
      permissionLevel: ['admin'],
      icon: 'user'
    },
    {
      name: 'jobs',
      permissionLevel: ['admin'],
      icon: 'bolt'
    },
    {
      name: 'requests',
      permissionLevel: ['employee', 'admin'],
      icon: 'question circle'
    },
    {
      name: 'timecard',
      permissionLevel: ['employee'],
      icon: 'clock'
    },
    {
      name: 'schedule',
      permissionLevel: ['employee'],
      icon: 'calendar alternate'
    }
  ]

  renderOptions = () => {
    switch (this.props.user.type) {
      case 'employee':
        return this.menuOptions.map(option => {
          if (option.permissionLevel.indexOf('employee') != -1) {
            return (
              <Menu.Item as='a' onClick={() => props.handleDisplayChange(option.name)}>
                <Icon name={option.icon} />
                {option.name}
              </Menu.Item>
            )
          }
        })
      case 'admin':
      case 'super':
        return this.menuOptions.map(option => {
          if (option.permissionLevel.indexOf('admin') != -1) {
            return (
              <Menu.Item as='a' onClick={() => props.handleDisplayChange(option.name)}>
                <Icon name={option.icon} />
                {option.name}
              </Menu.Item>
            )
          }
        })
      default: return null
    }
  }


  render() {
    return (
      <React.Fragment>
        <Header as='h3'>Admin</Header>
        {this.renderOptions}
      </React.Fragment>
    )
  }
}

export default AdminSidebar
