import React from 'react'
import { BrowserRouter as Router, Route, Link } from 'react-router-dom'

import Dashboard from './components/Dashboard'
import Nav from './components/Nav'

class App extends React.Component {

  state = {
    showMenu: true,
    currentTime: new Date(),
    userFirstName: 'Steven'
  }

  componentDidMount() {
    this.setTime()
  }

  handleMenuChange = () => this.setState({ showMenu: !this.state.showMenu })

  setTime = () => {
    setInterval(() => {
      this.setState({ currentTime: new Date() })
    }, 1000)
  }

  render() {
    return (
      <Router >
        <div id='App'>
          <Nav handleMenuChange={this.handleMenuChange} time={this.state.currentTime} userFirstName={this.state.userFirstName} />
          <Route exact path='/' component={Login} />
          <Route
            path='/dashboard'
            render={() => <Dashboard showMenu={this.state.showMenu} handleMenuChange={this.handleMenuChange} time={this.state.currentTime} />}
          />
        </div>
      </Router>
    )
  }
}

function Login() {
  return <h2>Login</h2>
}

export default App
