import React from 'react'

class Clock extends React.Component {

  state = { time: 'Loading...' }

  updateTime = () => setInterval(this.setState({ time: Date.now() }), 1000)

  render() {
    this.updateTime()
    return (
      <div id='clock'>
        this.state.time
      </div>
    )
  }
}

export default Clock
