import React from 'react'
import { BrowserRouter as Router, Route, Link } from 'react-router-dom'

import Dashboard from './components/Dashboard'
import CreateEmployee from './components/CreateEmployee'
import Nav from './components/Nav'
import Login from './components/Login'

import local from './api/local'

class App extends React.Component {

  state = {
    showMenu: true,
    currentTime: new Date(),
    userFirstName: 'Steven'
  }

  componentDidMount() {
    this.setTime()
  }

  handleMenuChange = () => this.setState({ showMenu: !this.state.showMenu })

  setTime = () => {
    setInterval(() => {
      this.setState({ currentTime: new Date() })
    }, 1000)
  }

  createEmployee = async values => {
    const response = await local.post(`/employee/new`, values)
    const data = response.data
    if (!data.success) {
      this.handleError(data.message)
      // } else if (data.token) {
      //   this.setState({ token: data.token })
      //   storeToken(data.token)
    }
  }

  render() {
    return (
      <Router >
        <div id='App'>
          <Nav handleMenuChange={this.handleMenuChange} currentTime={this.state.currentTime} userFirstName={this.state.userFirstName} />
          <Route exact path='/' component={Login} />
          <Route
            path='/dashboard'
            render={() => <Dashboard showMenu={this.state.showMenu} handleMenuChange={this.handleMenuChange} time={this.state.currentTime} />}
          />
          <Route
            path='/login'
            render={() => <Login />}
          />
          <Route
            path='/createEmployee'
            render={() => <CreateEmployee createUser={this.createEmployee} />}
          />
        </div>
      </Router>
    )
  }
}

export default App
