import React from 'react'
import { BrowserRouter as Router, Route, Redirect } from 'react-router-dom'

import Dashboard from './components/Dashboard'
import CreateEmployee from './components/CreateEmployee'
import Nav from './components/Nav'
import Login from './components/Login'

import local from './api/local'
import { retrieveToken, storeToken } from './utils/handleToken.js'


class App extends React.Component {

  state = {
    showMenu: true,
    currentTime: new Date(),
    userFirstName: 'Steven',
    token: null
  }

  componentDidMount() {
    const token = retrieveToken('EMP')
    this.setTime()
  }

  handleMenuChange = () => this.setState({ showMenu: !this.state.showMenu })

  setTime = () => {
    setInterval(() => {
      this.setState({ currentTime: new Date() })
    }, 1000)
  }

  createEmployee = async values => {
    const response = await local.post(`/employee/new`, values)
    const data = response.data
    if (!data.success) {
      this.handleError(data.message)
    } else if (data.token) {
      this.setState({ token: data.token })
      storeToken(data.token)
    }
  }

  login = async credentials => {
    const response = await local.post(`/employee/login`, credentials)
    const data = response.data
    if (!data.success) {
      this.handleError(data.message)
    } else if (data.token) {
      storeToken(data.token)
      this.setState({ token: data.token })
      this.getUserAssets(data.token)
    }
  }

  render() {
    return (
      <Router >
        <div id='App'>
          <Nav handleMenuChange={this.handleMenuChange} currentTime={this.state.currentTime} userFirstName={this.state.userFirstName} />
          <Route exact path='/' component={Login} />
          <Route
            path='/dashboard'
            render={() => {
              if (this.state.token) {
                return <Dashboard showMenu={this.state.showMenu} handleMenuChange={this.handleMenuChange} time={this.state.currentTime} />
              } else {
                return <Redirect to={{ pathname: '/login' }} />
              }
            }}
          />
          <Route
            path='/login'
            render={() => <Login login={this.login} />}
          />
          <Route
            path='/createEmployee'
            render={() => <CreateEmployee createUser={this.createEmployee} />}
          />
        </div>
      </Router>
    )
  }
}

export default App
